import React from 'react';

const Header = () => {
  const getCurrentTime = () => {
    const currentDate = new Date();
    const currentTime = currentDate.toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
    });
    return currentTime;
  };

  return (
    <header>
      <h1>My To-Do List</h1>
      <div className="clock">{getCurrentTime()}</div>
    </header>
  );
};

export default Header;
